];

// æ¯ç±»å•†å“çš„iconåº“
const goodsIcons: Record<number, string[]> = {
  1: ["ğŸ", "ğŸŒ", "ğŸ‡", "ğŸ‰", "ğŸ“"],
  2: ["ğŸ¥©", "ğŸ—", "ğŸ–", "ğŸ¥“", "ğŸ¤"],
  3: ["ğŸ¥¦", "ğŸ¥•", "ğŸŒ½", "ğŸ†", "ğŸ¥’"],
  4: ["ğŸ¥¤", "ğŸ§ƒ", "ğŸ¥›", "ğŸµ", "â˜•"],
  5: ["ğŸª", "ğŸ«", "ğŸ¿", "ğŸ©", "ğŸ°"]
};

// æ´—ç‰Œ
export function shuffle<T>(arr: T[]): T[] {
  const a = arr.slice();
  for (let i = a.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}

// ç”Ÿæˆ20ä¸ªå•†å“ï¼Œç±»åˆ«åˆ†å¸ƒéšæœº
export function generateGoods(): Goods[] {
  let left = 20;
  const counts: number[] = [];
  for (let i = 0; i < categories.length; i++) {
    let count =
      i === categories.length - 1
        ? left
        : Math.floor(Math.random() * (left - (categories.length - i - 1))) + 1;
    counts.push(count);
    left -= count;
  }
  const goods: Goods[] = [];
  categories.forEach((cat, idx) => {
    for (let i = 0; i < counts[idx]; i++) {
      const iconList = goodsIcons[cat.id];
      goods.push({
        id: `${cat.id}-${i}-${Math.random().toString(36).slice(2, 6)}`,
        name: `${cat.name}${i + 1}`,
        categoryId: cat.id,
        icon: iconList[i % iconList.length]
      });
    }
  });
  return shuffle(goods);
}
